package main

import (
	"fmt"
	"strings"
)

func main() {
	santa(input())
}

func santa(input string) int {
	inputToArray := strings.Split(input, "")
	floor := 0

	for _, bracket := range inputToArray {
		switch bracket {
		case "(":
			floor++
		case ")":
			floor--
		}
	}

	fmt.Println(floor)
	return floor
}

